import React, { useEffect, useState } from 'react'
import { Row, Col, Container, Card, CardBody } from 'reactstrap';
import Chart from './Chart';
import Filter from './Filter';
import axios from 'axios';
const SearchStatic = () => {

    //성별, 연령대 선택 객체
    const [filter, setFilter] = useState([])
    const [gender, setGender] = useState("")
    const [age, setAge] = useState("")

    const addGenderFilter = (e)=>{
        console.log('성별 필터 추가', e.target.value);
        setGender(e.target.value)
    }

    const addAgeFilter = (e)=>{
        console.log('연령대 필터 추가', e.target.value);
        setAge(e.target.value)
    }

    const loadData = ()=>{
        console.log('loadData');
        axios.get("http://localhost:8085/haru/main")
        .then((res)=>{
        
            //성별, 연령 조건에 따른 jsaon 객체 선택
            if(gender=='w' && age == 10){
                let temp = []
                temp.push(res.data[0].recNutri)
                temp.pop(res.data[0].recNutri.rec_target_seq)
                temp.push(res.data[1].recNutri)
                temp.push(res.data[2].recNutri)
                setFilter(temp);
            }
        }).catch((e)=>{
            console.log('error', e);
        })
    }

    useEffect(()=>{
        console.log(filter);
    },[filter])



    // useEffect(()=>{
    //     axios.get("http://localhost:8085/haru/main")
    //     .then((res)=>{
    //     console.log('res: ', res.data);
    //     }).catch((e)=>{
    //         console.log('error', e);
    //     })
    // },[])

  return (
    <div>
        <Container>
        <Row className="justify-content-center">
            <Col md="7" className="text-center">
                <h2 className="title">최근 3개월간 사람들이 많이 찾은 영양제는?</h2>
            </Col>
        </Row>
        <Row className="m-t-40">
           <Chart data={filter}/>
           <Filter loadData={loadData} addGenderFilter={addGenderFilter} addAgeFilter={addAgeFilter}/>
        </Row>
    </Container>
    </div>
  )
}

export default SearchStatic
