import React, { useEffect, useState } from 'react'
import { Row, Col, Container, Card, CardBody } from 'reactstrap';
import FloatDetailFunc from './FloatDetailFunc';

const DetailFunc = ({func, btnClick, visible, setVisible}) => {

    const [title, setTitle] = useState("");
    const [nutri, setNutri] = useState("");
    const [effect, setEffect] = useState([])

    useEffect(()=>{
        if(title == "혈행흐름개선"){
            setNutri("오메가3")
            setEffect("오메가3는 생선이나 해조류에서 추출한 여러 종류의 불포화지방산을 부르는 말이에요. 그 중 가장 중요한 것은 EPA와 DHA에요.|EPA는 간에서 중성지방 합성을 줄여 혈중 중성지방 농도를 낮춰주고, 혈액순환 개선에 도움을 줘요.|DHA는 뇌, 신경 조직, 망막의 중요 구성 성분으로 뇌세포를 손상시키는 물질을 감소시켜, 기억력을 높이는 데 도움을 줄 수 있어요. 또, 건조한 눈을 개선해줘요.")
        }else if(title == "장 건강"){
            setNutri("유산균")
            setEffect("")
        }else if(title == "간 건강"){
            setNutri("밀크씨슬")
            setEffect("")
        }else if(title == "눈 건강"){
            setNutri("루테인&지아잔틴")
            setEffect("")
        }else if(title == "관절/뼈 건강"){
            setNutri("보스웰리아")
            setEffect("")
        }else{
            setNutri("비타민C")
            setEffect("")
        }
    },[title])




  return (
    <div>
        {func.map((item)=>(
            <Col md="4">
            <Card className="card-shadow">
                <a href="#" className="img-ho" onClick={(e)=>{
                    setVisible(!visible)
                    setTitle(e.target.name);
                }}><img className="card-img-top" alt="wrappixel kit" src={item.url} name={item.effect}/></a>
                <CardBody>
                    <h5 className="font-medium m-b-0">{item.effect}</h5>
                </CardBody>
            </Card>
        </Col>
        ))}

        {visible && <FloatDetailFunc title={title} nutri={nutri} effect={effect} />}

    </div>
  )
}

export default DetailFunc
