import React, { useEffect } from 'react'
import { Container, Progress, Row, Col } from 'reactstrap'
import axios from 'axios'
import { useState } from 'react'

const RecAmountNutri = ({item}) => {

  const [list, setList] = useState([])
  const [min, setMin] = useState()
  const [max, setMax] = useState()

  useEffect(()=>{
    axios.get('http://localhost:3000/haru/preview/nutri.json')
    .then((res)=>{
      console.log('통신성공');
      console.log('res', res.data);
      setList(res.data)

    })
  }, [])

  useEffect(()=>{
    console.log(list);
    // let index = list.findIndex(e => e.name == `${item.nutri_name}`)
    // if(index !== -1){
    //   console.log(index);
    // }
  },[list])
  
  list.filter(e => e.name == item.nutri_name)

  return (
    <div>
      <Container>
        <Row>
          <Col>{item.nutri_name}</Col>
          <Col>
          <Container>
            <Row><Col>성인기준 일 권장량 대비 80%</Col></Row>
            <Row>
              <Col>
                <Progress
                  value={item.nutri_content}
                  min={10}
                  max={20}
                >{item.nutri_content}</Progress>
              </Col>          
            </Row>
            <Row>
              <Col>10mg</Col>
              <Col>20mg</Col>
            </Row>
          </Container>
          </Col>
        </Row>
      </Container>


    </div>
  )
}

export default RecAmountNutri
