import React from 'react'
import { Button, Row, Col, Container, Card, FormGroup, Input } from 'reactstrap';

const BloodWishList = ({bloodList, boneList, checkedItemHandler}) => {
    console.log('bloodList', bloodList);
    //하나의 체크박스 관리
    const [bChecked, setChecked] = useState(false)
    const checkHandler = ({target})=>{
        setChecked(!bChecked)
        checkedItemHandler((BoneWishList.id, target.checked))
    } 

  return (
    <div>
        <h5>혈관/혈행흐름개선</h5>

        <FormGroup>
        <Container>
            <Row className="m-t-40 justify-content-center">
        {bloodList.map((item)=>(
                <Col lg="4" md="6">
                    <Input value={item.wishlist.product_id} name="bloodCk" type="checkbox" checked={bChecked} onChange={(e) => checkHandler(e)}></Input>
                    <Card>
                        <img className="card-img-top" alt="wrappixel kit" src={item.wishlist.img}/>
                        <h5 className="font-medium m-t-30">{item.wishlist.detail_name}</h5>
                        <p className="m-t-20">{item.wishlist.detail_price}원</p>
                        <p className="m-t-20">{item.wishlist.manufacturer}</p>
                    </Card>
                </Col>
        ))}
        </Row>
        </Container>
        </FormGroup>
    </div>
  )
}

export default BloodWishList
